string sort_numbers(string numbers) {
    // Step 1: Create a mapping from number words to their integer values
    map<string, int> word_to_num = {
        {"zero", 0}, {"one", 1}, {"two", 2}, {"three", 3}, {"four", 4},
        {"five", 5}, {"six", 6}, {"seven", 7}, {"eight", 8}, {"nine", 9}
    };

    // Step 2: Tokenize the input string
    vector<string> tokens;
    stringstream ss(numbers);
    string token;
    while (ss >> token) {
        tokens.push_back(token);
    }

    // Step 3: Convert to integers
    vector<int> nums;
    for (const string& word : tokens) {
        nums.push_back(word_to_num[word]);
    }

    // Step 4: Sort the integers
    sort(nums.begin(), nums.end());

    // Step 5: Convert back to words
    vector<string> sorted_words;
    for (int num : nums) {
        for (const auto& pair : word_to_num) {
            if (pair.second == num) {
                sorted_words.push_back(pair.first);
                break;
            }
        }
    }

    // Step 6: Join the words
    string result;
    for (size_t i = 0; i < sorted_words.size(); ++i) {
        result += sorted_words[i];
        if (i < sorted_words.size() - 1) {
            result += " ";
        }
    }

    return result;
}

