pair<float, float> find_closest_elements(vector<float> numbers) {
    // Ensure the vector has at least two elements
    if (numbers.size() < 2) {
        throw invalid_argument("Vector must contain at least two elements.");
    }

    // Initialize variables to store the closest pair and the smallest difference
    pair<float, float> closest_pair = {numbers[0], numbers[1]};
    float smallest_diff = abs(numbers[0] - numbers[1]);

    // Iterate through all pairs of numbers
    for (size_t i = 0; i < numbers.size(); ++i) {
        for (size_t j = i + 1; j < numbers.size(); ++j) {
            float diff = abs(numbers[i] - numbers[j]);
            if (diff < smallest_diff) {
                smallest_diff = diff;
                closest_pair = {numbers[i], numbers[j]};
            }
        }
    }

    // Ensure the pair is returned in ascending order
    if (closest_pair.first > closest_pair.second) {
        swap(closest_pair.first, closest_pair.second);
    }

    return closest_pair;
}

