}

// Function to generate the n-th prime Fibonacci number
int prime_fib(int n) {
    if (n <= 0) return -1; // Invalid input
    int a = 0, b = 1, count = 0;
    while (true) {
        int temp = a + b;
        a = b;
        b = temp;
        if (is_prime(b)) {
            count++;
            if (count == n) {
                return b;
            }
        }
    }
}

