int fib4(int n) {
    // Base cases
    if (n == 0) return 0;
    if (n == 1) return 0;
    if (n == 2) return 2;
    if (n == 3) return 0;

    // Initialize the first four elements of the sequence
    int a = 0, b = 0, c = 2, d = 0;
    int result = 0;

    // Compute the n-th element iteratively
    for (int i = 4; i <= n; ++i) {
        result = a + b + c + d;
        a = b;
        b = c;
        c = d;
        d = result;
    }

    return result;
}

