#include <stdio.h>

int modp(int n, int p) {
    if (n == 0) {
        return 1;
    }

    long long result = 1;
    long long base = 2;

    while (n > 0) {
        // If n is odd, multiply base with result
        if (n % 2 == 1) {
            result = (result * base) % p;
        }
        // n must be even now
        n = n / 2;
        base = (base * base) % p;  // Change base to base^2
    }

    return (int)result;
}

