    for (int i = 0; i < lst.size(); ++i) {
        for (int j = 0; j < lst[i].size(); ++j) {
            if (lst[i][j] == x) {
                coordinates.push_back({i, j});
            }
        }
    }

    // Sort coordinates by rows in ascending order
    // For coordinates within the same row, sort by columns in descending order
    sort(coordinates.begin(), coordinates.end(), [](const vector<int>& a, const vector<int>& b) {
        if (a[0] == b[0]) {
            return a[1] > b[1];
        }
        return a[0] < b[0];
    });

    return coordinates;
}

