    for (char c : s) {
        if (c >= 'a' && c <= 'z') {
            // Shift the character by 4 places
            char newChar = ((c - 'a' + 4) % 26) + 'a';
            encrypted += newChar;
        } else if (c >= 'A' && c <= 'Z') {
            // Shift the character by 4 places (for uppercase letters)
            char newChar = ((c - 'A' + 4) % 26) + 'A';
            encrypted += newChar;
        } else {
            // If the character is not a letter, leave it unchanged
            encrypted += c;
        }
    }
    return encrypted;
}

