    for (int i = 1; i <= n; ++i) {
        if (i % 2 == 0) {
            // Compute factorial of i
            int factorial = 1;
            for (int j = 1; j <= i; ++j) {
                factorial *= j;
            }
            result[i - 1] = factorial;
        } else {
            // Compute sum of numbers from 1 to i
            int sum = 0;
            for (int j = 1; j <= i; ++j) {
                sum += j;
            }
            result[i - 1] = sum;
        }
    }

    return result;
}

