bool move_one_ball(vector<int> arr) {
    int n = arr.size();
    if (n == 0) return true; // Empty vector is considered sorted

    int dipCount = 0;
    int dipIndex = -1;

    // Find the dip
    for (int i = 0; i < n; ++i) {
        if (arr[i] > arr[(i + 1) % n]) {
            dipCount++;
            dipIndex = i;
        }
    }

    // There should be exactly one dip
    if (dipCount != 1) return false;

    // Check if the segments are sorted
    for (int i = 0; i < n - 1; ++i) {
        int current = (dipIndex + 1 + i) % n;
        int next = (dipIndex + 1 + i + 1) % n;
        if (arr[current] > arr[next]) {
            return false;
        }
    }

    return true;
}

