#include <vector>
#include <queue>
#include <climits>
using namespace std;

vector<int> minPath(vector<vector<int>> grid, int k) {
    int N = grid.size();
    vector<int> min_path(k, INT_MAX); // Initialize with maximum possible values
    queue<pair<vector<int>, pair<int, int>>> q; // Queue to store paths and coordinates

    // Directions for moving in the grid (up, down, left, right)
    vector<pair<int, int>> directions = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}};

    // Start BFS from each cell in the grid
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            q.push({{grid[i][j]}, {i, j}});
        }
    }

    while (!q.empty()) {
        auto current = q.front();
        q.pop();

        vector<int> current_path = current.first;
        int x = current.second.first;
        int y = current.second.second;

        // If the current path length is k, compare it with the minimum path
        if (current_path.size() == k) {
            if (current_path < min_path) {
                min_path = current_path;
            }
            continue;
        }

        // Explore all possible moves
        for (auto dir : directions) {
            int new_x = x + dir.first;
            int new_y = y + dir.second;

            // Check if the new position is within the grid
            if (new_x >= 0 && new_x < N && new_y >= 0 && new_y < N) {
                vector<int> new_path = current_path;
                new_path.push_back(grid[new_x][new_y]);
                q.push({new_path, {new_x, new_y}});
            }
        }
    }

    return min_path;
}

