vector<int> tri(int n) {
    // Vector to store the Tribonacci sequence
    vector<int> tribonacci(n + 1);

    // Base case
    tribonacci[0] = 1; // This is just a placeholder since we start from tri(1)
    tribonacci[1] = 3;

    // Compute the Tribonacci sequence for n >= 2
    for (int i = 2; i <= n; ++i) {
        if (i % 2 == 0) {
            // Even case
            tribonacci[i] = 1 + i / 2;
        } else {
            // Odd case
            tribonacci[i] = tribonacci[i - 1] + tribonacci[i - 2] + tribonacci[i + 1];
        }
    }

    return tribonacci;
}

