string fix_spaces(string text) {
    string result;
    int n = text.length();
    int i = 0;

    while (i < n) {
        if (text[i] == ' ') {
            int count = 0;
            // Count consecutive spaces
            while (i < n && text[i] == ' ') {
                count++;
                i++;
            }
            // If more than 2 consecutive spaces, replace with '-'
            if (count > 2) {
                result += '-';
            } else {
                // Replace 1 or 2 spaces with underscores
                for (int j = 0; j < count; j++) {
                    result += '_';
                }
            }
        } else {
            // If not a space, just add the character to the result
            result += text[i];
            i++;
        }
    }

    return result;
}

