string file_name_check(string file_name) {
    // Check for the number of digits
    int digit_count = 0;
    for (char c : file_name) {
        if (isdigit(c)) {
            digit_count++;
            if (digit_count > 3) {
                return "No";
            }
        }
    }

    // Check for exactly one dot
    size_t dot_pos = file_name.find('.');
    if (dot_pos == string::npos || dot_pos == 0 || dot_pos == file_name.size() - 1) {
        return "No";
    }
    if (file_name.find('.', dot_pos + 1) != string::npos) {
        return "No";
    }

    // Check if the substring before the dot starts with a letter
    string before_dot = file_name.substr(0, dot_pos);
    if (before_dot.empty() || !isalpha(before_dot[0])) {
        return "No";
    }

    // Check if the substring after the dot is one of the allowed extensions
    unordered_set<string> allowed_extensions = {"txt", "exe", "dll"};
    string after_dot = file_name.substr(dot_pos + 1);
    if (allowed_extensions.find(after_dot) == allowed_extensions.end()) {
        return "No";
    }

    return "Yes";
}

