            lst[i] = lst[i] * lst[i]; // Square the entry if index is a multiple of 3
        } else if (i % 4 == 0 && i % 3 != 0) {
            lst[i] = lst[i] * lst[i] * lst[i]; // Cube the entry if index is a multiple of 4 and not a multiple of 3
