vector<string> bf(string planet1, string planet2) {
    // Define the order of planets
    vector<string> planets = {"Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"};

    // Create a map to store the index of each planet
    unordered_map<string, int> planetIndex;
    for (int i = 0; i < planets.size(); ++i) {
        planetIndex[planets[i]] = i;
    }

    // Check if the input planets are valid
    if (planetIndex.find(planet1) == planetIndex.end() || planetIndex.find(planet2) == planetIndex.end()) {
        return {};
    }

    // Get the indices of the input planets
    int index1 = planetIndex[planet1];
    int index2 = planetIndex[planet2];

    // Ensure index1 is less than index2 for consistency
    if (index1 > index2) {
        swap(index1, index2);
    }

    // Collect the planets between index1 and index2 (excluding the endpoints)
    vector<string> result;
    for (int i = index1 + 1; i < index2; ++i) {
        result.push_back(planets[i]);
    }

    return result;
}

