    string strongest_extension = "";
    int max_strength = INT_MIN;

    for (const string& ext : extensions) {
        int CAP = 0;
        int SM = 0;

        // Calculate CAP and SM for the current extension
        for (char c : ext) {
            if (isupper(c)) {
                CAP++;
            } else if (islower(c)) {
                SM++;
            }
        }

        // Calculate the strength of the current extension
