string int_to_mini_roman(int number) {
    // Define the mapping of values to their corresponding Roman numeral symbols in lowercase
    vector<pair<int, string>> roman_map = {
        {1000, "m"}, {900, "cm"}, {500, "d"}, {400, "cd"},
        {100, "c"}, {90, "xc"}, {50, "l"}, {40, "xl"},
        {10, "x"}, {9, "ix"}, {5, "v"}, {4, "iv"},
        {1, "i"}
    };

    string result = "";

    // Iterate through the mapping
    for (const auto& pair : roman_map) {
        int value = pair.first;
        string symbol = pair.second;

        // Append the symbol to the result string as many times as the value fits into the number
        while (number >= value) {
