string find_max(vector<string> words) {
    string max_word = "";
    int max_unique_count = 0;

    for (const string& word : words) {
        // Use an unordered_set to count unique characters
        unordered_set<char> unique_chars(word.begin(), word.end());
        int unique_count = unique_chars.size();

        // If the current word has more unique characters, update max_word
        if (unique_count > max_unique_count) {
            max_word = word;
            max_unique_count = unique_count;
        }
        // If the current word has the same number of unique characters but is lexicographically smaller, update max_word
        else if (unique_count == max_unique_count && word < max_word) {
            max_word = word;
        }
    }

    return max_word;
}

