using namespace std;

int do_algebra(vector<string> operato, vector<int> operand) {
    // Initialize the result with the first operand
    int result = operand[0];

    // Iterate through the operators and operands
    for (size_t i = 0; i < operato.size(); ++i) {
        // Get the current operator and operand
        string op = operato[i];
        int next_operand = operand[i + 1];

        // Apply the operator to the result and the next operand
        if (op == "+") {
            result += next_operand;
        } else if (op == "-") {
            result -= next_operand;
        } else if (op == "*") {
            result *= next_operand;
        } else if (op == "//") {
            result /= next_operand; // Note: This is integer division
        } else if (op == "**") {
            result = pow(result, next_operand);
        }
    }

    // Return the final result
    return result;
}

