vector<string> separate_paren_groups(string paren_string) {
    vector<string> result;
    string current_group;
    stack<char> s;
    bool in_group = false;

    for (char c : paren_string) {
        if (c == ' ') {
            continue; // Ignore spaces
        }

        if (c == '(') {
            if (!in_group) {
                in_group = true;
                current_group = "";
            }
            s.push(c);
        } else if (c == ')') {
            if (!s.empty() && s.top() == '(') {
                s.pop();
                current_group += c;
                if (s.empty()) {
                    result.push_back(current_group);
                    in_group = false;
                }
            } else {
                // This should not happen if input is valid
                throw invalid_argument("Invalid input: unbalanced parentheses");
            }
        } else {
            if (in_group) {
                current_group += c;
            }
        }
    }

    return result;
}

