bool is_palindrome(const string& str) {
    // Test if given string is a palindrome
    string s(str.rbegin(), str.rend());
    return s == str;
}

string make_palindrome(const string& str) {
    // Find the shortest palindrome that begins with a supplied string
    for (size_t i = 0; i < str.size(); ++i) {
        // Check if the suffix from i to the end is a palindrome
        if (is_palindrome(str.substr(i))) {
            // If it is, append the reverse of the prefix before this suffix
            string prefix = str.substr(0, i);
            reverse(prefix.begin(), prefix.end());
            return str + prefix;
        }
    }
    // If no palindromic suffix is found, append the reverse of the entire string
    string reversed_str(str.rbegin(), str.rend());
    return str + reversed_str;
}

