string sort_numbers(string numbers) {
    // Step 1: Create a mapping from number words to their corresponding numerical values
    map<string, int> word_to_num = {
        {"zero", 0}, {"one", 1}, {"two", 2}, {"three", 3}, {"four", 4},
        {"five", 5}, {"six", 6}, {"seven", 7}, {"eight", 8}, {"nine", 9}
    };

    // Step 2: Tokenize the input string
    stringstream ss(numbers);
    string token;
    vector<string> tokens;
    while (ss >> token) {
        tokens.push_back(token);
    }

    // Step 3: Sort the tokens based on their numerical values
    sort(tokens.begin(), tokens.end(), [&](const string &a, const string &b) {
        return word_to_num[a] < word_to_num[b];
    });

    // Step 4: Reconstruct the output string
    string sorted_numbers;
    for (size_t i = 0; i < tokens.size(); ++i) {
        if (i != 0) {
            sorted_numbers += " ";
        }
        sorted_numbers += tokens[i];
    }

    return sorted_numbers;
}

