bool is_prime(long long n) {
    // Handle edge cases
    if (n <= 1) {
        return false;
    }
    if (n == 2 || n == 3) {
        return true;
    }
    if (n % 2 == 0 || n % 3 == 0) {
        return false;
    }

    // Check for factors from 5 to sqrt(n)
    for (long long i = 5; i * i <= n; i += 6) {
        if (n % i == 0 || n % (i + 2) == 0) {
            return false;
        }
    }

    return true;
}

#include<stdio.h>
#include<math.h>
#include<vector>
using namespace std;

double poly(vector<double> xs, double x){
    /* 
    Evaluates polynomial with coefficients xs at point x. return xs[0] + xs[1] * x + xs[1] * x^2 + .... xs[n] * x^n 
    */
    double sum = 0;
    for (int i = 0; i < xs.size(); i++)
    {
        sum += xs[i] * pow(x, i);
    }
    return sum;
}

double poly_derivative(vector<double> xs, double x){
    /* 
    Evaluates the derivative of the polynomial with coefficients xs at point x.
    */
    double sum = 0;
    for (int i = 1; i < xs.size(); i++)
    {
        sum += i * xs[i] * pow(x, i - 1);
    }
    return sum;
}

double find_zero(vector<double> xs){
    /*
    xs are coefficients of a polynomial. find_zero find x such that poly(x) = 0. find_zero returns only one zero point, even if there are many. 
    Moreover, find_zero only takes list xs having even number of coefficients and largest non zero coefficient as it guarantees a solution.
    */
    double x0 = 0.0; // Initial guess
    double tolerance = 1e-7; // Tolerance for convergence
    int max_iterations = 1000; // Maximum number of iterations

    for (int i = 0; i < max_iterations; i++) {
        double fx = poly(xs, x0);
        double dfx = poly_derivative(xs, x0);

        if (fabs(fx) < tolerance) {
            return x0; // Converged to a root
        }

        if (fabs(dfx) < tolerance) {
            printf("Derivative is too small, Newton-Raphson method may not converge.\n");
            return x0;
        }

        x0 = x0 - fx / dfx; // Newton-Raphson update
    }

    printf("Maximum iterations reached without convergence.\n");
    return x0;
}

