    int largest_factor = 1;

    // Check for the smallest prime factor, which is 2
    while (n % 2 == 0) {
        largest_factor = 2;
        n /= 2;
    }

    // Check for odd factors from 3 onwards
    for (int i = 3; i * i <= n; i += 2) {
        while (n % i == 0) {
            largest_factor = i;
            n /= i;
        }
    }

    // If n is still greater than 2, then n itself is a prime factor
    if (n > 2) {
        largest_factor = n;
    }

    return largest_factor;
}

