        // If a is negative, we need to handle the cube root of a negative number
        a = -a;
        double cube_root = cbrt(a);
        int n = static_cast<int>(cube_root);
        return (n * n * n == a);
    } else {
        // If a is non-negative, we can directly take the cube root
        double cube_root = cbrt(a);
        int n = static_cast<int>(cube_root);
        return (n * n * n == a);
    }
}

