using namespace std;

string solve(int N) {
    // Step 1: Calculate the sum of digits
    int sum = 0;
    int temp = N;
    while (temp > 0) {
        sum += temp % 10;  // Add the last digit to the sum
        temp /= 10;        // Remove the last digit
    }

    // Step 2: Convert the sum to binary
    string binarySum = bitset<32>(sum).to_string();  // Convert to binary string

    // Step 3: Remove leading zeros
    size_t pos = binarySum.find('1');  // Find the first '1'
    if (pos != string::npos) {
        binarySum = binarySum.substr(pos);  // Extract the substring from the first '1'
    } else {
        binarySum = "0";  // If no '1' is found, the sum was 0
    }

    return binarySum;
}

