string encrypt(string s) {
    string result = "";
    int shift = 4; // 2 * 2 = 4

    for (char c : s) {
        if (c >= 'a' && c <= 'z') {
            // Shift within lowercase letters
            c = 'a' + (c - 'a' + shift) % 26;
        } else if (c >= 'A' && c <= 'Z') {
            // Shift within uppercase letters
            c = 'A' + (c - 'A' + shift) % 26;
        }
        result += c;
    }

    return result;
}

