int next_smallest(const std::vector<int>& lst) {
    if (lst.size() < 2) {
        return -1; // Return -1 as a sentinel value for None
    }

    // Create a copy of the vector and sort it
    std::vector<int> sorted_lst = lst;
    std::sort(sorted_lst.begin(), sorted_lst.end());

    // Find the second smallest unique element
    int smallest = sorted_lst[0];
    for (size_t i = 1; i < sorted_lst.size(); ++i) {
        if (sorted_lst[i] != smallest) {
