// Function to count the number of ones in the binary representation of an integer
int countOnes(int num) {
    int count = 0;
    while (num != 0) {
        count += num & 1;
        num >>= 1;
    }
    return count;
}

// Custom comparator function for sorting
bool customComparator(int a, int b) {
    int onesA = countOnes(a);
    int onesB = countOnes(b);
    if (onesA == onesB) {
        return a < b; // If number of ones is the same, sort by decimal value
    }
    return onesA < onesB; // Otherwise, sort by number of ones
}

// Function to sort the array based on the custom comparator
vector<int> sort_array(vector<int> arr) {
    sort(arr.begin(), arr.end(), customComparator);
    return arr;
}

