bool is_consonant(char c) {
    // Convert the character to lowercase to handle both uppercase and lowercase letters
    c = tolower(c);
    // Check if the character is a consonant
    return (c >= 'a' && c <= 'z') && !(c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u');
}

vector<string> select_words(string s, int n) {
    vector<string> result;
    if (s.empty()) {
        return result;
    }

    // Use a stringstream to split the string into words
    stringstream ss(s);
    string word;

    while (ss >> word) {
        int consonant_count = 0;
        // Count the number of consonants in the current word
        for (char c : word) {
            if (is_consonant(c)) {
                consonant_count++;
            }
        }
        // If the word has exactly n consonants, add it to the result vector
        if (consonant_count == n) {
            result.push_back(word);
        }
    }

    return result;
}

