string match_parens(vector<string> lst) {
    int open1 = 0, close1 = 0, open2 = 0, close2 = 0;

    // Count parentheses in the first string
    for (char c : lst[0]) {
        if (c == '(') open1++;
        else if (c == ')') close1++;
    }

    // Count parentheses in the second string
    for (char c : lst[1]) {
        if (c == '(') open2++;
        else if (c == ')') close2++;
    }

    // Check if we can balance the parentheses
    int total_open = open1 + open2;
    int total_close = close1 + close2;

    // The total number of opening and closing parentheses must be equal
    if (total_open != total_close) {
        return "No";
    }

    // Check if the number of closing parentheses in one string does not exceed the number of opening parentheses in the other string
    if (close1 > open2 || close2 > open1) {
        return "No";
    }

    return "Yes";
}

