vector<int> get_odd_collatz(int n) {
    vector<int> odd_numbers;

    // Start with the given number
    while (n != 1) {
        if (n % 2 == 1) { // Check if the number is odd
            odd_numbers.push_back(n);
        }
        // Apply the Collatz rule
        if (n % 2 == 0) {
            n = n / 2;
        } else {
            n = 3 * n + 1;
        }
    }

    // Add the final 1 to the list of odd numbers
    odd_numbers.push_back(1);

    // Sort the list of odd numbers
    sort(odd_numbers.begin(), odd_numbers.end());

    return odd_numbers;
}

