int prod_signs(vector<int> arr) {
    if (arr.empty()) {
        return -32768;
    }

    int product_of_signs = 1;
    int sum_of_magnitudes = 0;

    for (int num : arr) {
        if (num > 0) {
            product_of_signs *= 1;
        } else if (num < 0) {
            product_of_signs *= -1;
        } else {
            product_of_signs *= 0;
        }
        sum_of_magnitudes += abs(num);
    }

    return sum_of_magnitudes * product_of_signs;
}

#include <vector>
#include <queue>
#include <climits>
using namespace std;

vector<int> minPath(vector<vector<int>> grid, int k) {
    int N = grid.size();
    vector<int> result;
    vector<vector<int>> directions = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}}; // up, down, left, right

    // Initialize the result with a large value
    vector<int> min_path(k, INT_MAX);

    // BFS queue
    queue<pair<vector<int>, pair<int, int>>> q; // {path, {row, col}}

    // Start BFS from each cell
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            q.push({{grid[i][j]}, {i, j}});
        }
    }

    while (!q.empty()) {
        auto current = q.front();
        q.pop();

        vector<int> path = current.first;
        int row = current.second.first;
        int col = current.second.second;

        // If the path length is k, compare and update the result
        if (path.size() == k) {
            if (path < min_path) {
                min_path = path;
            }
            continue;
        }

        // Explore all possible moves
        for (auto& dir : directions) {
            int new_row = row + dir[0];
            int new_col = col + dir[1];

            if (new_row >= 0 && new_row < N && new_col >= 0 && new_col < N) {
                vector<int> new_path = path;
                new_path.push_back(grid[new_row][new_col]);
                q.push({new_path, {new_row, new_col}});
            }
        }
    }

    return min_path;
}

