int digits(int n) {
    // Convert the integer to a string
    string numStr = to_string(n);

    // Initialize the product to 1 (multiplicative identity)
    int product = 1;

    // Flag to check if any odd digit is found
    bool hasOdd = false;

    // Iterate through each character in the string
    for (char ch : numStr) {
        // Convert the character to an integer
        int digit = ch - '0';

        // Check if the digit is odd
        if (digit % 2 != 0) {
            // Multiply the product by the odd digit
            product *= digit;
            hasOdd = true;
        }
    }

    // If no odd digit was found, return 0
    if (!hasOdd) {
        return 0;
    }

    // Return the product of the odd digits
    return product;
}

