bool is_nested(string str) {
    int open_brackets = 0;
    int close_brackets = 0;

    for (char ch : str) {
        if (ch == '[') {
            open_brackets++;
        } else if (ch == ']') {
            if (open_brackets > 0) {
                open_brackets--;
                close_brackets++;
            }
        }
    }

    // If there is more than one close bracket, it means there was nesting
    return close_brackets > 1;
}

