vector<int> largest_smallest_integers(vector<int> lst) {
    int largest_negative = INT_MIN; // Initialize to the smallest possible integer
    int smallest_positive = INT_MAX; // Initialize to the largest possible integer

    for (int num : lst) {
        if (num < 0) {
            // Update largest_negative if num is larger than the current largest_negative
            if (num > largest_negative) {
                largest_negative = num;
            }
        } else if (num > 0) {
            // Update smallest_positive if num is smaller than the current smallest_positive
            if (num < smallest_positive) {
                smallest_positive = num;
            }
        }
    }

    // If no negative number was found, set largest_negative to 0
    if (largest_negative == INT_MIN) {
        largest_negative = 0;
    }

    // If no positive number was found, set smallest_positive to 0
    if (smallest_positive == INT_MAX) {
        smallest_positive = 0;
    }

    return {largest_negative, smallest_positive};
}

