using namespace std;

boost::any compare_one(boost::any a, boost::any b) {
    // Helper function to convert any type to double
    auto to_double = [](const boost::any& value) -> double {
        if (value.type() == typeid(int)) {
            return static_cast<double>(boost::any_cast<int>(value));
        } else if (value.type() == typeid(float)) {
            return static_cast<double>(boost::any_cast<float>(value));
        } else if (value.type() == typeid(double)) {
            return boost::any_cast<double>(value);
        } else if (value.type() == typeid(string)) {
            string str = boost::any_cast<string>(value);
            boost::replace_all(str, ",", ".");
            return stod(str);
        }
        throw std::invalid_argument("Unsupported type");
    };

    // Convert both values to double for comparison
    double a_val = to_double(a);
    double b_val = to_double(b);

    // Compare the values
    if (a_val > b_val) {
        return a;
    } else if (b_val > a_val) {
        return b;
    } else {
        return "None";
    }
}

