string fix_spaces(string text) {
    string result;
    int n = text.length();
    int i = 0;

    while (i < n) {
        if (text[i] == ' ') {
            int count = 0;
            // Count consecutive spaces
            while (i < n && text[i] == ' ') {
                count++;
                i++;
            }
            // Replace with underscore if single space
            if (count == 1) {
                result += '_';
            }
            // Replace with hyphen if more than two consecutive spaces
            else if (count > 2) {
                result += '-';
            }
            // If exactly two spaces, replace each with underscore
            else if (count == 2) {
                result += "__";
            }
        } else {
            // Append non-space characters directly
            result += text[i];
            i++;
        }
    }

    return result;
}

