string file_name_check(string file_name) {
    int dot_count = 0;
    int digit_count = 0;
    int dot_index = -1;

    // Iterate through the file name to count dots and digits
    for (int i = 0; i < file_name.length(); i++) {
        if (file_name[i] == '.') {
            dot_count++;
            dot_index = i;
        } else if (isdigit(file_name[i])) {
            digit_count++;
        }
    }

    // Check if there are more than three digits
    if (digit_count > 3) {
        return "No";
    }

    // Check if there is exactly one dot
    if (dot_count != 1) {
        return "No";
    }

    // Check if the substring before the dot is not empty and starts with a letter
    string before_dot = file_name.substr(0, dot_index);
    if (before_dot.empty() || !isalpha(before_dot[0])) {
        return "No";
    }

    // Check if the substring after the dot is one of the allowed extensions
    string after_dot = file_name.substr(dot_index + 1);
    if (after_dot != "txt" && after_dot != "exe" && after_dot != "dll") {
        return "No";
    }

    // If all conditions are met, return "Yes"
    return "Yes";
}

