int sum_squares(vector<int> lst) {
    int sum = 0;

    for (size_t i = 0; i < lst.size(); ++i) {
        if (i % 3 == 0) {
            // Square the element if the index is a multiple of 3
            lst[i] = lst[i] * lst[i];
        } else if (i % 4 == 0 && i % 3 != 0) {
            // Cube the element if the index is a multiple of 4 but not a multiple of 3
            lst[i] = lst[i] * lst[i] * lst[i];
        }
        // Add the transformed element to the sum
        sum += lst[i];
    }

    return sum;
}

