// Function to parse the fraction string and return the numerator and denominator
pair<int, int> parseFraction(const string& fraction) {
    size_t pos = fraction.find('/');
    int numerator = stoi(fraction.substr(0, pos));
    int denominator = stoi(fraction.substr(pos + 1));
    return {numerator, denominator};
}

// Function to compute the greatest common divisor (GCD) of two numbers
int gcd(int a, int b) {
    while (b != 0) {
        int temp = b;
        b = a % b;
        a = temp;
    }
    return a;
}

// Function to simplify the product of two fractions and check if it's a whole number
bool simplify(string x, string n) {
    // Parse the fractions
    pair<int, int> fracX = parseFraction(x);
    pair<int, int> fracN = parseFraction(n);

    // Compute the product of the two fractions
    int numerator = fracX.first * fracN.first;
    int denominator = fracX.second * fracN.second;

    // Check if the resulting fraction simplifies to a whole number
    int commonDivisor = gcd(numerator, denominator);
    numerator /= commonDivisor;
    denominator /= commonDivisor;

    return denominator == 1;
}

