vector<string> bf(string planet1, string planet2) {
    // List of planets in order of their proximity to the Sun
    vector<string> planets = {"Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"};

    // Check if both planet names are valid
    if (find(planets.begin(), planets.end(), planet1) == planets.end() ||
        find(planets.begin(), planets.end(), planet2) == planets.end()) {
        return {};
    }

    // Find the positions of planet1 and planet2 in the list
    int pos1 = find(planets.begin(), planets.end(), planet1) - planets.begin();
    int pos2 = find(planets.begin(), planets.end(), planet2) - planets.begin();

    // Ensure pos1 is less than pos2 for consistency
    if (pos1 > pos2) {
        swap(pos1, pos2);
    }

    // Extract the planets between pos1 and pos2 (excluding planet1 and planet2)
    vector<string> result(planets.begin() + pos1 + 1, planets.begin() + pos2);

    return result;
}

