#include <stdio.h>
#include <math.h>
#include <vector>
using namespace std;

double poly(vector<double> xs, double x) {
    /* 
    Evaluates polynomial with coefficients xs at point x. 
    return xs[0] + xs[1] * x + xs[2] * x^2 + .... xs[n] * x^n 
    */
    double sum = 0;
    for (int i = 0; i < xs.size(); i++) {
        sum += xs[i] * pow(x, i);
    }
    return sum;
}

vector<double> poly_derivative(vector<double> xs) {
    /* 
    Computes the derivative of the polynomial with coefficients xs. 
    */
    vector<double> derivative;
    for (int i = 1; i < xs.size(); i++) {
        derivative.push_back(i * xs[i]);
    }
    return derivative;
}

double find_zero(vector<double> xs) {
    /*
    xs are coefficients of a polynomial. find_zero finds x such that poly(x) = 0. 
    find_zero returns only one zero point, even if there are many. 
    Moreover, find_zero only takes list xs having even number of coefficients and 
    largest non zero coefficient as it guarantees a solution.
    */
    double x0 = 0.0; // Initial guess
    double tolerance = 1e-6; // Tolerance for convergence
    int max_iterations = 1000; // Maximum number of iterations

    vector<double> derivative_xs = poly_derivative(xs);

    for (int i = 0; i < max_iterations; i++) {
        double fx = poly(xs, x0);
        double dfx = poly(derivative_xs, x0);

        if (fabs(fx) < tolerance) {
            return x0; // Converged to a root
        }

        if (dfx == 0) {
            // Avoid division by zero
            x0 += 1.0; // Adjust initial guess
            continue;
        }

        x0 = x0 - fx / dfx; // Newton-Raphson update
    }

    return x0; // Return the best guess after max iterations
}

