vector<int> sort_third(vector<int> l) {
    // Step 1: Identify the indices divisible by 3
    vector<int> indices;
    for (size_t i = 0; i < l.size(); ++i) {
        if ((i + 1) % 3 == 0) {  // Indices are 0-based, so we check (i + 1) % 3
            indices.push_back(i);
        }
    }

    // Step 2: Extract the values at these indices
    vector<int> values;
    for (int index : indices) {
        values.push_back(l[index]);
    }

    // Step 3: Sort the extracted values
    sort(values.begin(), values.end());

    // Step 4: Replace the original values with the sorted values
    for (size_t i = 0; i < indices.size(); ++i) {
        l[indices[i]] = values[i];
    }

    // Step 5: Return the modified vector
    return l;
}

