// Function to generate the n-th prime Fibonacci number
int prime_fib(int n) {
    if (n <= 0) return -1; // Invalid input
    int a = 0, b = 1, count = 0;
    while (true) {
        int next = a + b;
        a = b;
        b = next;
        if (is_prime(next)) {
            count++;
            if (count == n) {
                return next;
            }
        }
    }
}

#include <vector>
#include <algorithm>
using namespace std;

bool triples_sum_to_zero(vector<int> l) {
    // Sort the vector
    sort(l.begin(), l.end());

    int n = l.size();

    // Iterate through the vector
    for (int i = 0; i < n - 2; ++i) {
        // Skip duplicates for the first element
        if (i > 0 && l[i] == l[i - 1]) continue;

        int left = i + 1;
        int right = n - 1;

        while (left < right) {
            int sum = l[i] + l[left] + l[right];

            if (sum == 0) {
                return true;
            } else if (sum < 0) {
                // Move the left pointer to the right to increase the sum
                ++left;
            } else {
                // Move the right pointer to the left to decrease the sum
                --right;
            }
        }
    }

    return false;
}

