#include <stdio.h>

int modp(int n, int p) {
    if (n == 0) {
        return 1; // 2^0 % p is always 1
    }

    long long result = 1;
    long long base = 2;

    while (n > 0) {
        if (n % 2 == 1) {
            result = (result * base) % p;
        }
        base = (base * base) % p;
        n /= 2;
    }

    return result;
}

