vector<int> pluck(vector<int> arr) {
    // Initialize variables to store the smallest even value and its index
    int smallest_even = -1;
    int smallest_index = -1;

    // Iterate through the vector
    for (int i = 0; i < arr.size(); ++i) {
        // Check if the current element is even
        if (arr[i] % 2 == 0) {
            // If it's the first even number found or it's smaller than the current smallest even number
            if (smallest_even == -1 || arr[i] < smallest_even) {
                smallest_even = arr[i];
                smallest_index = i;
            }
        }
    }

    // If an even number was found, return the smallest even number and its index
    if (smallest_even != -1) {
        return {smallest_even, smallest_index};
    } else {
        // If no even number was found, return an empty vector
        return {};
    }
}

