int search(vector<int> lst) {
    // Step 1: Count the frequency of each integer
    unordered_map<int, int> frequencyMap;
    for (int num : lst) {
        frequencyMap[num]++;
    }

    // Step 2: Find the greatest integer that meets the criteria
    int maxValidInteger = -1;
    for (const auto& pair : frequencyMap) {
        int num = pair.first;
        int freq = pair.second;
        if (freq >= num) {
            if (num > maxValidInteger) {
                maxValidInteger = num;
            }
        }
    }

    // Step 3: Return the result
    return maxValidInteger;
}

