bool is_simple_power(int x, int n) {
    // Special case: if n is 1, the only power of 1 is 1 itself
    if (n == 1) {
        return x == 1;
    }

    // Iterate through possible values of k
    int power = 1;
    while (power < x) {
        power *= n;
        if (power == x) {
            return true;
        }
    }

    return false;
}

