        // Swap the case of the letter
        if (islower(c)) {
            c = toupper(c);
        } else if (isupper(c)) {
            c = tolower(c);
        }

        // Replace vowels with the letter that appears 2 places ahead
        if (c == 'a' || c == 'A') {
            c = (c == 'a') ? 'c' : 'C';
        } else if (c == 'e' || c == 'E') {
            c = (c == 'e') ? 'g' : 'G';
        } else if (c == 'i' || c == 'I') {
            c = (c == 'i') ? 'k' : 'K';
        } else if (c == 'o' || c == 'O') {
            c = (c == 'o') ? 'q' : 'Q';
        } else if (c == 'u' || c == 'U') {
            c = (c == 'u') ? 'w' : 'W';
        }

        encodedMessage += c;
    }
    return encodedMessage;
}

